import React, { useState, useEffect } from "react";
import Lobby from "./Lobby";
import Game from "./Game";
import GameOverScreen from "./GameOverScreen";
import socket from "./socket";

function App() {
    const [playerData, setPlayerData] = useState({});
    const [gameOverData, setGameOverData] = useState(null);
    const [phase, setPhase] = useState("menu"); // "menu" | "lobby" | "game" | "gameover"

    useEffect(() => {
        socket.on("return_to_lobby", () => {
            console.log("🔁 [App] return_to_lobby received");
            setGameOverData(null);
            setPhase("lobby");
        });

        socket.on("game_started", ({ players, turnIndex, playerId }) => {
            console.log("🎮 [App] game_started received");
            setPlayerData(prev => ({
                ...prev,
                players,
                turnIndex,
                playerId
            }));
            setGameOverData(null);
            setPhase("game");
        });

        return () => {
            socket.off("return_to_lobby");
            socket.off("game_started");
        };
    }, []);

    return (
        <div className="app">
            {phase === "menu" && (
                <Lobby
                    setPhase={setPhase}
                    setPlayerData={setPlayerData}
                    isRejoining={false}
                />
            )}

            {phase === "lobby" && (
                <Lobby
                    setPhase={setPhase}
                    setPlayerData={setPlayerData}
                    isRejoining={true}
                    playerData={playerData}
                />
            )}

            {phase === "game" && (
                <Game
                    playerData={playerData}
                    setPhase={setPhase}
                    setGameOverData={setGameOverData}
                />
            )}

            {phase === "gameover" && (
                <GameOverScreen
                    data={gameOverData}
                    setGameOverData={setGameOverData}
                    setPhase={setPhase}
                />
            )}
        </div>
    );
}

export default App;
