import React, { useState, useEffect } from "react";
import Lobby from "./Lobby";
import Game from "./Game";
import GameOverScreen from "./GameOverScreen";
import socket from "./socket";

function App() {
    const [inGame, setInGame] = useState(false);
    const [playerData, setPlayerData] = useState({});
    const [gameOverData, setGameOverData] = useState(null);

    useEffect(() => {
        const handleReturnToLobby = () => {
            console.log("🔁 [App] return_to_lobby received");
            setGameOverData(null); 
            setInGame(false);
        };

        const handleGameStarted = ({ players, turnIndex, playerId }) => {
            console.log("🎮 [App] game_started received");

            setPlayerData(prev => ({
                ...prev,
                players,
                turnIndex,
                playerId
            }));

            setGameOverData(null);
            setInGame(true);
        };

        socket.on("return_to_lobby", handleReturnToLobby);
        socket.on("game_started", handleGameStarted);

        return () => {
            socket.off("return_to_lobby", handleReturnToLobby);
            socket.off("game_started", handleGameStarted);
        };
    }, []);

    return (
        <div className="app">
            {gameOverData ? (
                <GameOverScreen
                    data={gameOverData}
                    setGameOverData={setGameOverData}
                    setInGame={setInGame}
                    playerId={playerData.playerId}
                    roomCode={playerData.roomCode}
                />
            ) : inGame ? (
                <Game
                    playerData={playerData}
                    setInGame={setInGame}
                    setGameOverData={setGameOverData}
                />
            ) : (
                <Lobby
                    setInGame={setInGame}
                    setPlayerData={setPlayerData}
                />
            )}
        </div>
    );
}

export default App;
