import React, { useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3001");

function Game({ playerData }) {
  const [guess, setGuess] = useState("");
  const [feedback, setFeedback] = useState("");
  const [hasGuessed, setHasGuessed] = useState(false);

  const handleGuess = () => {
    if (!guess.trim()) return;

    // Find out what the real item is for this player
    const yourName = playerData.players.find(p => p.item === "???")?.name;
    const actualItem = playerData.players.find(p => p.name === yourName)?.actualItem;

    if (!actualItem) return;

    if (guess.trim().toLowerCase() === actualItem.toLowerCase()) {
      setFeedback("‚úÖ Correct! You guessed it!");
    } else {
      setFeedback("‚ùå Not quite. Try again next round!");
    }

    setHasGuessed(true);
  };

  return (
    <div className="game">
      <h1>Guess What's on Your Head!</h1>
      <ul>
        {playerData.players.map((p, i) => (
          <li key={i}>
            {p.name} ‚Üí {p.item}
          </li>
        ))}
      </ul>

      {!hasGuessed && (
        <>
          <input
            placeholder="Enter your guess"
            value={guess}
            onChange={(e) => setGuess(e.target.value)}
          />
          <button onClick={handleGuess}>Submit Guess</button>
        </>
      )}

      {feedback && <p>{feedback}</p>}
      <p>(Your item is hidden üëÄ)</p>
    </div>
  );
}

export default Game;
