import React, { useState, useEffect } from "react";
import socket from "./socket";

function Game({ playerData, setInGame }) {
    const [players, setPlayers] = useState(playerData.players || []);
    const [guess, setGuess] = useState("");
    const [feedback, setFeedback] = useState("");
    const [hasGuessed, setHasGuessed] = useState(false);
    const [turnIndex, setTurnIndex] = useState(playerData.turnIndex || 0);

    const yourPlayerId = playerData.playerId;

    useEffect(() => {
        const handleGameStarted = ({ players, turnIndex }) => {
            setPlayers(players);
            setFeedback("");
            setGuess("");
            setHasGuessed(false);
            setTurnIndex(turnIndex);

        };

        const handleNewRound = ({ players, message, turnIndex }) => {
            setPlayers(players);
            setFeedback("");
            setGuess("");
            setHasGuessed(false);
            setTurnIndex(turnIndex);

        };


        const handleGuessResult = ({ correct, points }) => {
            if (correct) {
                setFeedback("‚úÖ Correct! You earned points!");
            } else {
                setFeedback("‚ùå Not quite. Try again next round.");
                setGuess("")
            }
            setHasGuessed(true);
        };
        socket.on("turn_updated", ({ turnIndex }) => {
            setTurnIndex(turnIndex);

            const currentTurnPlayer = players[turnIndex];
            const myPlayer = players.find(p => p.item === "???");

            if (currentTurnPlayer?.name === myPlayer?.name) {
                setHasGuessed(false);
                setFeedback("");
            }
        });
        socket.on("game_over", ({ winner, score }) => {
            alert(`üéâ ${winner} wins with ${score} points!`);
            window.location.reload(); // or redirect back to lobby screen
        });
        socket.on("back_to_lobby", ({ winner, score }) => {
            alert(`üéâ ${winner} won with ${score} points!`);
            setInGame(false); // ‚úÖ return to lobby
        });
        socket.on("game_started", handleGameStarted);
        socket.on("new_round", handleNewRound);
        socket.on("guess_result", handleGuessResult);
        return () => {
            socket.off("game_started", handleGameStarted);
            socket.off("new_round", handleNewRound);
            socket.off("guess_result", handleGuessResult);
            socket.off("turn_updated");
            socket.off("game_over");
            socket.off("back_to_lobby");
        };

    },[players, setInGame]);

    const handleGuess = () => {
        if (!guess.trim() || hasGuessed) return;
        socket.emit("submit_guess", {
            roomCode: playerData.roomCode,
            playerId: yourPlayerId,
            guess
        });
        setGuess("")
    };

    const handlePassTurn = () => {
        socket.emit("pass_turn", { roomCode: playerData.roomCode });
    };
    const currentTurnPlayer = players[turnIndex];

    const isMyTurn = currentTurnPlayer?.name === players.find(p => p.item === "???")?.name;

    return (
        <div className="game">
            <h1>Guess What's on Your Head!</h1>

            <h3 style={{ marginBottom: "20px", color: isMyTurn ? "#0f0" : "#fff" }}>
                üéØ {isMyTurn
                    ? "It's your turn!"
                    : `Waiting for ${players[turnIndex]?.name}...`}
            </h3>

            <h2 style={{ marginBottom: "10px" }}>üßë‚Äçü§ù‚Äçüßë Players:</h2>
            <div className="player-list">
                {players.map((p, i) => (
                    <div
                        key={i}
                        className="player"
                        style={{
                            fontWeight: i === turnIndex ? "bold" : "normal",
                            color: i === turnIndex ? "#0f0" : "#fff",
                            textAlign: "center"
                        }}
                    >
                        {p.name}{p.item === "???" ? " (You!)" : ""}
                    </div>
                ))}
            </div>

            <h2>ü§î What everyone got:</h2>
            <div className="player-reveal">
                {players.map((p, i) => (
                    <div key={i} className="player-reveal-entry">
                        {p.name} ‚Üí {p.item}
                    </div>
                ))}
            </div>
            <h2 style={{ marginTop: "30px" }}>üèÜ Leaderboard</h2>
            <div className="leaderboard">
                {players
                    .slice()
                    .sort((a, b) => (b.score ?? 0) - (a.score ?? 0))
                    .map((p, i) => (
                        <div key={i} className="leaderboard-entry">
                            <span>{i + 1}. {p.name}{p.item === "???" ? " (You!)" : ""}</span>
                            <span>üéØ {p.score ?? 0}</span>
                        </div>
                    ))}
            </div>

            {isMyTurn && !hasGuessed && (
                <>
                    <input
                        placeholder="Enter your guess"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                    />
                    <button onClick={handleGuess}>Submit Guess</button>
                    <button onClick={handlePassTurn}>Pass Turn</button>
                </>
            )}

            {feedback && <p>{feedback}</p>}
        </div>
    );
}

export default Game;
