import React, { useState } from "react";
import Lobby from "./Lobby";
import Game from "./Game";
import GameOverScreen from "./GameOverScreen"; // ✅ Import the Game Over screen

function App() {
    const [inGame, setInGame] = useState(false);
    const [playerData, setPlayerData] = useState({});
    const [gameOverData, setGameOverData] = useState(null); // ✅ Game over state

    return (
        <div className="app">
            {gameOverData ? (
                <GameOverScreen
                    data={gameOverData}
                    setGameOverData={setGameOverData}
                    setInGame={setInGame}
                />
            ) : inGame ? (
                <Game
                    playerData={playerData}
                    setInGame={setInGame}
                    setGameOverData={setGameOverData} // ✅ Pass this down
                />
            ) : (
                <Lobby
                    setInGame={setInGame}
                    setPlayerData={setPlayerData}
                />
            )}
        </div>
    );
}

export default App;
