import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3001");

function Lobby({ setInGame, setPlayerData }) {
  const [name, setName] = useState("");
  const [roomCode, setRoomCode] = useState("");
  const [players, setPlayers] = useState([]);
  const [category, setCategory] = useState("food");
  const [isHost, setIsHost] = useState(false);

  useEffect(() => {
    socket.on("update_players", (players) => {
      setPlayers(players);
    });

    socket.on("game_started", (data) => {
      setPlayerData(data);
      setInGame(true);
    });

    return () => {
      socket.off("update_players");
      socket.off("game_started");
    };
  }, [setInGame, setPlayerData]);

  const handleCreate = () => {
    if (!name) return;
    socket.emit("create_room", { name }, ({ roomCode }) => {
      setRoomCode(roomCode);
      setIsHost(true);
    });
  };

  const handleJoin = () => {
    if (!name || !roomCode) return;
    socket.emit("join_room", { name, roomCode }, (res) => {
      if (res.error) alert(res.error);
    });
  };

  const handleStart = () => {
    socket.emit("set_category", { roomCode, category });
    socket.emit("start_game", roomCode);
  };

  return (
    <div className="lobby">
      <h1>What's on My Head</h1>

      {!roomCode ? (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={handleCreate}>Create Room</button>
          <hr />
          <input
            placeholder="Enter room code"
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
          />
          <button onClick={handleJoin}>Join Room</button>
        </>
      ) : (
        <>
          <p>Room Code: <strong>{roomCode}</strong></p>
          <h2>Players:</h2>
          <ul>
            {players.map((p) => (
              <li key={p.id}>{p.name}</li>
            ))}
          </ul>

          {isHost && (
            <>
              <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              >
                <option value="food">Food</option>
                <option value="animal">Animal</option>
                <option value="tool">Tool</option>
                <option value="object">Object</option>
              </select>
              <button onClick={handleStart}>Start Game</button>
            </>
          )}
        </>
      )}
    </div>
  );
}

export default Lobby;
