import React, { useEffect } from "react";
import socket from "./socket";

function GameOverScreen({ data, setGameOverData, setPhase }) {
    const isHost = socket.id === data.hostId;

    const handlePlayAgain = () => {
        if (isHost) {
            console.log("üîÅ Host clicked Play Again, emitting return_to_lobby");
            socket.emit("return_to_lobby", { roomCode: data.roomCode });
        }
    };

    useEffect(() => {
        socket.on("return_to_lobby", () => {
            console.log("‚úÖ Received return_to_lobby. Switching to lobby phase.");
            setGameOverData(null);
            setPhase("lobby");
        });

        return () => {
            socket.off("return_to_lobby");
        };
    }, [setPhase, setGameOverData]);

    return (
        <div className="game">
            <h1>üéâ Game Over!</h1>
            <h2>üèÜ Winner: {data.winner} with {data.score} points!</h2>

            <h3>üèÅ Final Leaderboard:</h3>
            <div className="leaderboard">
                {data.players
                    .slice()
                    .sort((a, b) => (b.score ?? 0) - (a.score ?? 0))
                    .map((p, i) => (
                        <div key={i} className="leaderboard-entry">
                            <span>{i + 1}. {p.name}</span>
                            <span>üéØ {p.score ?? 0}</span>
                        </div>
                    ))}
            </div>

            {isHost && (
                <button onClick={handlePlayAgain}>üîÅ Play Again</button>
            )}
        </div>
    );
}

export default GameOverScreen;
