import React, { useState, useEffect } from "react";
import socket from "./socket";

function Lobby({ setInGame, setPlayerData }) {
    const [name, setName] = useState("");
    const [roomCodeInput, setRoomCodeInput] = useState("");
    const [roomCode, setRoomCode] = useState("");
    const [players, setPlayers] = useState([]);
    const [category, setCategory] = useState("food");
    const [isHost, setIsHost] = useState(false);
    const [pointLimit, setPointLimit] = useState(10);

    useEffect(() => {
        socket.on("update_players", (players) => {
            setPlayers(players);
        });

        socket.on("game_started", (data) => {
            setPlayerData({
                ...data,
                roomCode,
                playerId: data.playerId
            });
            setInGame(true);
        });
        socket.on("return_to_lobby", () => {
            console.log("üëã Returned to lobby view");
            setInGame(false);
        });

        return () => {
            socket.off("update_players");
            socket.off("game_started");
            socket.off("return_to_lobby");
        };
    }, [setInGame, setPlayerData, roomCode]);

    const handleCreate = () => {
        if (!name || name.length < 3 || name.length > 20) {
            alert("Name must be between 3 and 20 characters.");
            return;
        }
        socket.emit("create_room", { name }, ({ roomCode }) => {
            setRoomCode(roomCode);
            setIsHost(true);
        });
    };

    const handleJoin = () => {
        if (!name || name.length < 3 || name.length > 20) {
            alert("Name must be between 3 and 20 characters.");
            return;
        }
        if (!roomCodeInput) return;
        socket.emit("join_room", { name, roomCode: roomCodeInput }, (res) => {
            if (res.error) {
                alert(res.error);
            } else {
                setRoomCode(roomCodeInput);
                setIsHost(false);
            }
        });
    };

    const handleStart = () => {
        if (players.length < 2) {
            alert("You need at least 2 players to start the game.");
            return;
        }

        socket.emit("set_category", { roomCode, category });
        socket.emit("set_point_limit", { roomCode, pointLimit });
        socket.emit("start_game", roomCode);
    };


    return (
        <div className="lobby">
            <h1>What's on My Head</h1>

            {!roomCode ? (
                <>
                    <input
                        placeholder="Enter your name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <button onClick={handleCreate}>Create Room</button>

                    <input
                        placeholder="Enter room code"
                        value={roomCodeInput}
                        onChange={(e) => setRoomCodeInput(e.target.value)}
                        maxLength={5}
                    />
                    <button onClick={handleJoin}>Join Room</button>
                </>
            ) : (
                <>
                    <p>Room Code: <strong>{roomCode}</strong></p>
                    <h2>Players:</h2>
                    <div className="player-list">
                        {players.map((p) => (
                            <div key={p.id} className="player-name">
                                {p.name}
                            </div>
                        ))}
                    </div>

                    {isHost && (
                        <>
                                <label>Categories:</label>
                                <select
                                    value={category}
                                    onChange={(e) => setCategory(e.target.value)}
                                >
                                    <option value="random">üé≤ Random</option>
                                    <option value="food">üçΩÔ∏è Food</option>
                                    <option value="animal">üêæ Animal</option>
                                    <option value="object">üì¶ Object</option>
                                    <option value="movie">üé¨ Movie</option>
                                    <option value="celebrity">üé® Celebrity</option>
                                    <option value="country">üåç Country</option>
                                </select>

                                <label>Points to win:</label>
                                <select
                                    value={pointLimit}
                                    onChange={(e) => setPointLimit(Number(e.target.value))}
                                >
                                    <option value={5}>5</option>
                                    <option value={10}>10</option>
                                    <option value={15}>15</option>
                                </select>

                            <button onClick={handleStart}>Start Game</button>
                        </>
                    )}
                </>
            )}
        </div>
    );
}

export default Lobby;
