import React, { useState, useEffect } from "react";
import socket from "./socket";


function Game({ playerData }) {
    const [players, setPlayers] = useState(playerData.players);
    const [guess, setGuess] = useState("");
    const [feedback, setFeedback] = useState("");
    const [hasGuessed, setHasGuessed] = useState(false);
    const [score, setScore] = useState(0);
    const [isTurn, setIsTurn] = useState(false);

    const yourPlayerId = playerData.playerId;


    useEffect(() => {
        socket.on("guess_result", ({ correct, points }) => {
            if (correct) {
                setFeedback("‚úÖ Correct! You earned points!");
                setScore(points);
            } else {
                setFeedback("‚ùå Not quite. Try again next round.");
            }
            setHasGuessed(true);
        });

        socket.on("new_round", ({ players, message, isTurn }) => {
            setPlayers(players);
            setFeedback("");
            setGuess("");
            setHasGuessed(false);
            setIsTurn(isTurn);
        });

        socket.on("turn_updated", ({ isTurn }) => {
            setIsTurn(isTurn);
        });

        return () => {
            socket.off("guess_result");
            socket.off("new_round");
            socket.off("turn_updated")
        };
    }, []);

    const handleGuess = () => {
        if (!guess.trim() || hasGuessed) return;
        socket.emit("submit_guess", {
            roomCode: playerData.roomCode,
            playerId: yourPlayerId,
            guess
        });
    };

    return (
        <div className="game">
            <h1>Guess What's on Your Head!</h1>
            <div className="player-list">
                {players.map((p, i) => (
                    <div key={i} className="player">
                        {p.name} ‚Üí {p.item} {p.item === "???" ? "(You!)" : ""} | üèÜ {p.score ?? 0}
                    </div>
                ))}
            </div>

            {isTurn && !hasGuessed && (
                <>
                    <input
                        placeholder="Enter your guess"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                    />
                    <button onClick={handleGuess}>Submit Guess</button>
                    <button onClick={() => socket.emit("pass_turn", { roomCode: playerData.roomCode })}>
                        Pass Turn
                    </button>
                </>
            )}

            {feedback && <p>{feedback}</p>}
            <p>Your current score: {score}</p>
        </div>
    );
}

export default Game;
